import _ from 'lodash';
import logger from 'runtime/../../logger';
import moment from 'shared/moment';
import createESIGViewPDFModalWindow from 'runtime/sdk/components/ESIGViewPDFModalWindow.react';
import ft from 'shared/constants/FieldTypes';
import { FINISH_ESIG_UI_INTERNAL, COPY_NESTED_DATA_INTERNAL } from 'runtime/constants/MessageTypes';
import postMessageHelper from 'runtime/utils/postMessageHelper';
import { getEsiguiData } from 'runtime/nested/esigui/utils/esiguiDataTaker';
import { VERSION_ID } from 'runtime/nested/esigui/constants/globalKeys';
import { getClientDataMappingFromNestedToParent } from 'runtime/nested/esigui/utils/getClientDataMapping';
import mapSourceClientDataToTarget from 'runtime/nested/esigui/utils/mapSourceClientDataToTarget';
import shouldOpenInSeparateTab from 'runtime/nested/esigui/utils/shouldOpenInSeparateTab';
import { runtimeCoreApiInstance } from 'runtime/sdk/RuntimeCoreAPI';
import RuntimeActions from 'runtime/actions/RuntimeActionCreators';

const _addCapturedEvent = (context, data) => {
    if (!_.isArray(context.capturedEvents)) context.capturedEvents = [];

    if (!_.isPlainObject(data)) data = {};

    const { fieldName = '', fieldType = '', fieldValue = '', eventType = '' } = data;

    const event = {
        FieldName: fieldName,
        FieldType: fieldType,
        EventDate: moment.utc().format()
    };

    if (fieldType === ft.BUTTON) {
        event.FieldValue = '';
        event.EventType = 'click';
    } else if (fieldType === ft.RADIOBUTTON) {
        event.FieldValue = fieldValue;
        event.EventType = 'click';
    } else if (fieldType === ft.CHECKBOX) {
        if (fieldValue === 'true') event.EventType = 'checked';
        if (fieldValue === 'false') event.EventType = 'unchecked';
        event.FieldValue = '';
    } else {
        event.FieldValue = fieldValue;
        event.EventType = eventType;
    }

    context.capturedEvents.push(event);
};

const _getCapturedEvents = context => (_.isArray(context.capturedEvents) ? context.capturedEvents : []);

const _clearCapturedEvents = context => {
    context.capturedEvents = [];
};

/**
 * eSig UI SDK class. Used as separate SDK class for eSig UI applications.
 */
export class ESigEventHandlers {
    /**
     * This method is invoked whenever runtime executes the "capture event" action in eSigUI
     * @since v0.89.*
     * @param {Object} data - event data
     * @param {String} data.fieldName - name of a field that generated the event
     * @param {String} data.fieldType - type of a field that generated the event
     * @param {String} data.fieldValue - value of a field that generated the event
     * @param {String} data.eventType - type of the event on a field that generated the capture event (e.g. 'click', 'valuechanged', etc.)
     */
    async ESIGUI_captureEvent(data) {
        _addCapturedEvent(this, data);
    }

    async ESIGUI_flushAuditTrail() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;
        const capturedEvents = _getCapturedEvents(this);

        const authToken = await this.requestAuthToken();

        const { success, error, message } = await serviceRequestProvider.ESIGUI_flushAuditTrail(
            capturedEvents,
            authToken
        );

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_flushAuditTrail_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_flushAuditTrail_errorCode: { value: error, formattedValue: error },
                ESIGUI_flushAuditTrail_errorMessage: { value: message, formattedValue: message }
            }
        });

        _clearCapturedEvents(this);
    }

    /**
     * This method is invoked whenever runtime executes the "accept disclosure" action in eSigUI.
     * @since v0.89.*
     */
    async ESIGUI_acceptDisclosure() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const { success, error, message } = await serviceRequestProvider.ESIGUI_acceptDisclosure(authToken);

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_disclosureAcceptance_isSuccessful: {
                    value: success,
                    formattedValue: success
                },
                ESIGUI_disclosureAcceptance_errorCode: { value: error, formattedValue: error },
                ESIGUI_disclosureAcceptance_errorMessage: {
                    value: message,
                    formattedValue: message
                }
            }
        });
    }

    /**
     * This method is invoked whenever runtime executes the "accept consent" action in eSigUI.
     * @since v0.89.*
     */
    async ESIGUI_acceptConsent() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const { success, error, message } = await serviceRequestProvider.ESIGUI_acceptConsent(authToken);

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_consentAcceptance_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_consentAcceptance_errorCode: { value: error, formattedValue: error },
                ESIGUI_consentAcceptance_errorMessage: { value: message, formattedValue: message }
            }
        });
    }

    /**
     * This method is invoked whenever runtime executes the "decline disclosure" action in eSigUI.
     * @since v0.89.*
     */
    async ESIGUI_declineDisclosure() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const { success, error, message } = await serviceRequestProvider.ESIGUI_declineDisclosure(authToken);

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_disclosureDecline_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_disclosureDecline_errorCode: { value: error, formattedValue: error },
                ESIGUI_disclosureDecline_errorMessage: { value: message, formattedValue: message }
            }
        });
    }

    /**
     * This method is invoked whenever runtime executes the "decline consent" action in eSigUI.
     * @since v0.89.*
     */
    async ESIGUI_declineConsent() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const { success, error, message } = await serviceRequestProvider.ESIGUI_declineConsent(authToken);

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_consentDecline_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_consentDecline_errorCode: { value: error, formattedValue: error },
                ESIGUI_consentDecline_errorMessage: { value: message, formattedValue: message }
            }
        });
    }

    /**
     * This method is invoked whenever runtime executes the "view PDF" action in eSigUI (not to be confused with generic "view PDF", which is not eSig-specific)
     * @since v0.89.*
     */
    async ESIGUI_viewPDF() {
        const authToken = await this.requestAuthToken();

        const { data } = await this.coreApi.serviceRequestProvider.ESIGUI_getPDFsURL(authToken);
        const url = _.get(data, ['Signers', 0, 'DocumentViewUrl'], null);

        if (!url) throw new Error('ESIGUI: Url for viewing document (pdf) was not provided');

        if (shouldOpenInSeparateTab()) {
            const viewPdfWindow = window.open(url, 'View PDF');
            if (!viewPdfWindow) logger.runtime.esig.error('ESIG UI view pdf separate tab window is blocked by browser');
        } else {
            this.coreApi.showModal(
                createESIGViewPDFModalWindow({
                    pdfUrl: url,
                    onClose: this.coreApi.hideModal
                })
            );
        }
    }

    /**
     * This method is invoked whenever runtime executes the "get signing status" action in eSigUI (after the nested application receives a post message about signature completion)
     * @since v0.89.*
     * @returns {Promise<Object>} - promise of signing status
     */
    async ESIGUI_getSigningStatus() {
        const ANY_ERROR_STATUS = -1;
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const response = await serviceRequestProvider.ESIGUI_getSigningStatus(authToken);
        const { data, success, error, message } = response;
        const status = _.get(data, ['DynamicData', 'StatusId'], ANY_ERROR_STATUS);

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_vendorSigning_status: { value: status, formattedValue: status },
                ESIGUI_vendorSigning_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_vendorSigning_errorCode: { value: error, formattedValue: error },
                ESIGUI_vendorSigning_errorMessage: { value: message, formattedValue: message }
            }
        });

        return status;
    }

    /**
     * This method is invoked whenever runtime executes the "get vendor URL" action in eSigUI
     * @since v0.89.*
     * @returns {Promise<String>} - promise of the vendor URL
     */
    async ESIGUI_getVendorURL() {
        const { serviceRequestProvider } = this.coreApi;
        const clientDataKey = this.coreApi.runtimeStateItems.CLIENT_DATA;

        const authToken = await this.requestAuthToken();

        const response = await serviceRequestProvider.ESIGUI_getVendorURL(authToken);
        const { data, success, error, message } = response;

        this.coreApi.updateRuntimeState({
            [clientDataKey]: {
                ESIGUI_requestURL_isSuccessful: { value: success, formattedValue: success },
                ESIGUI_requestURL_errorCode: { value: error, formattedValue: error },
                ESIGUI_requestURL_errorMessage: { value: message, formattedValue: message }
            }
        });

        return _.get(data, ['Signers', 0, 'SigningUrl'], null);
    }

    /**
     * This method is invoked when nested application Runtime attempts to initialize with external data that has been transmitted from host application.
     * @since v0.92.*
     * @param {Object} data - ClientData from the host application, see format description in `SD Runtime fields data`;
     * @returns {undefined}
     */
    initialLoadData(data) {
        if (!data) return;

        this.coreApi.restoreRuntimeState({
            [this.coreApi.runtimeStateItems.CLIENT_DATA]: data
        });

        // To prevent case when nobody set current screen we should check it and update to first visible screen.
        const {
            [runtimeCoreApiInstance.runtimeStateItems.CURRENT_SCREEN]: currentScreenName
        } = runtimeCoreApiInstance.getRuntimeState(runtimeCoreApiInstance.runtimeStateItems.CURRENT_SCREEN);

        if (!currentScreenName) {
            RuntimeActions.updateCurrentScreenNoEmit();
        }
    }

    /**
     * This method is invoked whenever runtime executes the "finish signing" action in eSigUI
     * @since v0.92.*
     */
    async ESIGUI_finishSigning() {
        const nestedVersionRef = _.get(getEsiguiData(), VERSION_ID);
        postMessageHelper.sendMessageToParent({ type: FINISH_ESIG_UI_INTERNAL, nestedVersionRef });
    }

    /**
     * This method is invoked whenever runtime executes the "Copy data from Nested to Parent Version" action in eSigUI
     * @since v0.111.*
     */
    ESIGUI_copyNestedData() {
        const versionRef = _.get(getEsiguiData(), VERSION_ID);
        const { clientData: nestedClientData } = this.coreApi.getRuntimeState(
            this.coreApi.runtimeStateItems.CLIENT_DATA
        );
        const clientDataMapping = getClientDataMappingFromNestedToParent(versionRef);
        const eSigUiCaseData = mapSourceClientDataToTarget(nestedClientData, clientDataMapping);

        postMessageHelper.sendMessageToParent({
            type: COPY_NESTED_DATA_INTERNAL,
            payload: JSON.stringify(eSigUiCaseData)
        });
    }

    /**
     * This helper method knows how to get authentication token for requests to esig service.
     * @since v0.97.*
     * @returns {string}
     */
    async requestAuthToken() {
        const { serviceRequestProvider } = this.coreApi;
        let authToken;

        const maxNumberOfAttempts = 10; // TODO: take from config
        let counter = 0;
        while (!authToken && ++counter <= maxNumberOfAttempts) {
            try {
                authToken = await serviceRequestProvider.getEsigUIServiceAuthToken();
            } catch (e) {
                logger.runtime.coreApi.error(e, `failed to request ESIG authToken, attempt - ${counter}`);
            }
        }

        return authToken;
    }
}

import React from 'react';
import { runtimeCoreApiInstance } from 'runtime/sdk/RuntimeCoreAPI';

/* eslint no-unused-vars:0 */
/* istanbul ignore file  */

/**
 * Base SDK class.
 */
export class GlobalEventHandlers {
    /**
     * Reference to SDK API functions.
     * @example
     * ExtensionClass.prototype.saveData = function() {
     *     const clientData = this.coreApi.getRuntimeData();
     *     //...
     * }
     */
    coreApi; // declaring here just for documentation purposes. it actually doesn't affect the generated class

    /**
     * Superclass definition to provide access to implementation of base methods from SDK.
     * NOTE: default SDK implementation doesn't have any logic in base methods.
     * @example
     * ExtensionClass.prototype.callCustomAssembly = function(assemblyName) {
     *     return this.superclass.callCustomAssembly(assemblyName);
     * }
     */
    superclass; // declaring here just for documentation purposes. it actually doesn't affect the generated class

    /**
     * This method is invoked whenever Runtime makes a call to custom assembly (excluding custom assemblies for SourceGrid and SourceDropdown data retrieval).
     * @since v0.56.*
     * @param {String} assemblyName - name of custom assembly to call
     * @returns {Promise} - A Promise of operation completion if implementation contains any asynchronous operations. If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.callCustomAssembly = function(assemblyName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *     switch(assemblyName) {
     *         case: 'CustomAssembly1':
     *             // update runtime data
     *             break;
     *         // handle calls to other custom assemblies
     *     }
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.callCustomAssembly(assemblyName);
     * }
     */
    callCustomAssembly(assemblyName) {}

    /**
     * This method is invoked whenever Runtime makes a call to JavaScript custom assembly. Invocation occurs synchronously for Rule and Event action. It is allowed to return Promise and execute asynchronous code ONLY for Event action.
     * @since v0.77.*
     * @param {String} assemblyName - name of JavaScript custom assembly to call
     * @returns {undefined|Promise} - If implementation is synchronous you may return either undefined or resolved Promise. Return Promise of operation completion if implementation contains any asynchronous operations only valid for Event action.
     * @example
     * ExtensionClass.prototype.callJsCustomAssembly = function(assemblyName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *     switch(assemblyName) {
     *         case: 'JavaScriptCustomAssembly1':
     *             // update runtime data
     *             break;
     *         // handle calls to other JS custom assemblies
     *     }
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.callJsCustomAssembly(assemblyName);
     * }
     */
    callJsCustomAssembly(assemblyName) {}

    /**
     * This method is invoked whenever Runtime makes a call to server procedure.
     * @since v0.73.*
     * @param {String} procedureName - name of server procedure to call
     * @returns {Promise} - A Promise of operation completion if implementation contains any asynchronous operations. If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.callServerProcedure = function(procedureName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *     switch(procedureName) {
     *         case: 'Procedure1':
     *             // update runtime data
     *             break;
     *         // handle calls to other procedures
     *     }
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.callServerProcedure(procedureName);
     * }
     */
    callServerProcedure(procedureName) {}

    /**
     * Specific iGo method to apply e-signature
     * @since v0.73.*
     * @returns {Promise<Object>} - A Promise of operation completion if implementation contains any asynchronous operations and/or call to superclass.applyEsign (superclass.applyEsign returns a Promise). If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.applyEsign = function() {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.applyEsign();
     * }
     */
    applyEsign() {}

    /**
     * Specific iGo method to decline e-signature
     * @since v0.73.*
     * @returns {Promise<Object>} - A Promise of operation completion if implementation contains any asynchronous operations and/or call to superclass.declineEsign (superclass.declineEsign returns a Promise). If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.declineEsign = function() {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.declineEsign();
     * }
     */
    declineEsign() {}

    /**
     * Specific iGo method to activate form package
     * @since v0.73.*
     * @returns {Promise<Object>} - A Promise of operation completion if implementation contains any asynchronous operations and/or call to superclass.activateFormPackage (superclass.activateFormPackage returns a Promise). If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.activateFormPackage = function(name) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.activateFormPackage(name);
     * }
     */
    activateFormPackage() {}

    /**
     * Specific iGo method to prepare data before showing pdf
     * @since v0.73.*
     * @returns {Promise<Object>} - A Promise of operation completion if implementation contains any asynchronous operations and/or call to superclass.viewPDF (superclass.viewPDF returns a Promise). If implementation is synchronous you may return either resolved Promise or undefined.
     * @example
     * ExtensionClass.prototype.viewPDF = function() {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.viewPDF();
     * }
     */
    viewPDF() {}

    /**
     * This method is @deprecated and will be removed in nearest time. Use viewPDF() instead.
     */
    exportFormFromIGO() {}

    /**
     * This method is invoked whenever Runtime attempts to retrieve data for a SourceGrid.
     * @since v0.56.*
     * @param {String} assemblyName - name of custom assembly which returns Source Grid data, could be either JS or Server side custom assembly
     * @returns {String|Promise} - stringified JSON Array or Promise of such Array which contains data for Source Grid. You must return a Promise if implementation contains any asynchronous operations and/or call to superclass.getSourceGridData (superclass.getSourceGridData returns a Promise that resolves to requested data). If implementation is synchronous you may return either Promise resolved to stringified JSON Array or just stringified JSON Array with data.
     * @property {Object} JSON_Array[] - data object specifying data for single Source Grid row
     * @property {String} JSON_Array[].row-id - unique row id
     * @property {String} JSON_Array[].<column_name> - value for specified <column_name> in current row
     * @example
     * ExtensionClass.prototype.getSourceGridData = function(assemblyName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *     var data;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.getSourceGridData(assemblyName);
     *     // returned data example: '[{\"row-id\": \"1\",\"Name\": \"John\",\"DOB\": \"2016-10-10T00:00:00+00:00\"},{\"row-id\": \"2\",\"Name\": \"Bob\",\"DOB\": \"2010-02-13T00:00:00+00:00\"}]'
     * }
     */
    getSourceGridData(assemblyName) {}

    /**
     * This method is invoked whenever Runtime attempts to retrieve data for a SourceDropdown.
     * NOTE: its implementation should call services in background (don't use load progress indicator)
     * to avoid source dropdown list being closed after the data is loaded.
     * @since v0.56.*
     * @param {String} assemblyName - name of custom assembly which returns Source Dropdown data, could be either JS or Server side custom assembly
     * @returns {String|Promise} - stringified JSON Object or a Promise of such object which contains data for Source Dropdown. You must return a Promise if implementation contains any asynchronous operations and/or call to superclass.getSourceDropdownData (superclass.getSourceDropdownData returns a Promise that resolves to requested data). If implementation is synchronous you may return either Promise resolved to stringified JSON Object or just stringified JSON Object with data.
     * @property {Object[]} JSON_Object.records[] - array of records, each representing single data item for Source Dropdown
     * @property {String} JSON_Object.records[].name - Required. Item name (will be displayed in dropdown control)
     * @property {String} JSON_Object.records[].value - Required. Item value (internal data value)
     * @property {*} JSON_Object.records[].* - Optional. Any other supplementary data for a record
     * @example
     * ExtensionClass.prototype.getSourceDropdownData = function(assemblyName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.getSourceDropdownData(assemblyName);
     *     // returned data example: '{"records": [{"name": "Belarus","value": "Minsk","option1": "qwe","option2": "asd"},{"name": "USA","value": "Washington","option1": "zxc","option2": "rty"}]}'
     * }
     */
    getSourceDropdownData(assemblyName) {}

    /**
     * This method is invoked whenever Runtime attempts to validate credit card data via specific services.
     * @since v0.65.*
     * @param  {Object} creditCardData - filled in credit card data. Also contains extra parameters from client data. See `SD Runtime CreditCard control data` in <a href="manual/data%20format.html">Data Interchange Format description</a>
     * @param  {String} creditCardControlName
     * @param  {String} cardType - Visa, AmEx, MasterCard, etc...
     * @param  {Object} ftniRequestOptions - Request options to be sent to FTNI service (defaultStatus, doNotUpdateCustomer, doNotUseCustomerName, doNotUseCustomerAddress)
     * @returns {Promise<Object>} - Promise of the validateCreditCard operation completion. Resolving data format is the same as for @{creditCardData} param
     * @example
     * ExtensionClass.prototype.validateCreditCard = function(creditCardData, creditCardControlName, cardType) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.validateCreditCard(creditCardData, creditCardControlName, cardType);
     * }
     */
    validateCreditCard(creditCardData, creditCardControlName, cardType, ftniRequestOptions) {}

    /**
     * This method is used to erase previously entered credit card data.
     * @since v0.65.*
     * @param  {String} creditCardControlName - name of credit card control to be erased
     * @returns {Promise} - Promise of the changeCreditCard operation completion. No data need to be resolved
     * @example
     * ExtensionClass.prototype.changeCreditCard = function(creditCardControlName) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ... your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.changeCreditCard(creditCardControlName);
     * }
     */
    changeCreditCard(creditCardControlName) {}

    /**
     * This method is invoked whenever email control makes a call to validate email.
     * @since v0.67.*
     * @param {String} email - email to validate
     * @param {String} verificationService - verification service provider. One of ['kickbox']
     * @returns {Promise<Object>} - Promise of operation completion which should resolve an object
     * @property {String} Object.status - Required. Status of validation operation. One of ['valid', 'invalid', 'error', 'unverified']
     * @property {String} [Object.message] - Optional message about validation execution on service
     * @example
     * ExtensionClass.prototype.verifyEmail = function(email, verificationService) {
     *     // consider saving references to coreApi and superclass to avoid using "this" in nested functions:
     *     var coreApi = this.coreApi;
     *     var superclass = this.superclass;
     *
     *     // ...your code here
     *
     *     // return result of base method execution if you are extending the default implementation:
     *     return superclass.verifyEmail(email, verificationService);
     * }
     */
    verifyEmail(email, verificationService) {}

    createAISCase(controlName) {}

    openAISCase(controlName) {}

    underwriteAISCase(controlName, underwriteToken) {}

    /**
     * This method is invoked whenever Runtime attempts to receive Smarty Streets Address Predictions
     * @since v0.91.*
     * @param {String} value - according to this value address predictions will be suggested
     * @returns {Array} - Smarty Streets address predictions
     * Example of output data:
     * [
     *   {
     *     name: '22 Antelope Rd, Seneca MO',
     *     id: '22 Antelope Rd, Seneca MO',
     *     data: {
     *          street: '22 Antelope Rd',
     *          city: 'Seneca',
     *          state: 'MO'
     *     }
     *   },
