const ESIGUI_FINISH_SIGNING = 'ESIGUI_finishSigning';
const ESIGUI_COPY_NESTED_DATA = 'ESIGUI_copyNestedData';
const ESIGUI_CAPTURE_EVENT = 'ESIGUI_captureEvent';
const ESIGUI_FLUSH_AUDITTRAIL = 'ESIGUI_flushAuditTrail';
const ESIGUI_ACCEPT_DISCLOSURE = 'ESIGUI_acceptDisclosure';
const ESIGUI_ACCEPT_CONSENT = 'ESIGUI_acceptConsent';
const ESIGUI_DECLINE_DISCLOSURE = 'ESIGUI_declineDisclosure';
const ESIGUI_DECLINE_CONSENT = 'ESIGUI_declineConsent';
const ESIGUI_VIEW_PDF = 'ESIGUI_viewPDF';
const ESIGUI_GET_SIGNING_STATUS = 'ESIGUI_getSigningStatus';
const ESIGUI_GET_VENDOR_URL = 'ESIGUI_getVendorURL';

export { INITIAL_LOAD_DATA } from 'runtime/sdk/SDKEventHandlerNames';

export {
    ESIGUI_FINISH_SIGNING,
    ESIGUI_COPY_NESTED_DATA,
    ESIGUI_CAPTURE_EVENT,
    ESIGUI_FLUSH_AUDITTRAIL,
    ESIGUI_ACCEPT_DISCLOSURE,
    ESIGUI_ACCEPT_CONSENT,
    ESIGUI_DECLINE_DISCLOSURE,
    ESIGUI_DECLINE_CONSENT,
    ESIGUI_VIEW_PDF,
    ESIGUI_GET_SIGNING_STATUS,
    ESIGUI_GET_VENDOR_URL
};

// These constants should correspond to methods exported in GlobalEventHandlers class
// WARNING: using these constants in methods' names, like [SAVE_DATA](){} won't work for esdoc - documentation will have incorrect names

const SEND_EMAIL = 'sendEmail';
const APPLY_ESIGN = 'applyEsign';
const DECLINE_ESIGN = 'declineEsign';
const ACTIVATE_FORM_PACKAGE = 'activateFormPackage';
const EXPORT_FORM_FROM_IGO = 'exportFormFromIGO'; // This event is deprecated and will be remove in nearest time.
const VIEW_PDF = 'viewPDF';
const CALL_CUSTOM_ASSEMBLY = 'callCustomAssembly';
const CALL_JS_CUSTOM_ASSEMBLY = 'callJsCustomAssembly';
const CALL_SERVER_PROCEDURE = 'callServerProcedure';
const GET_SOURCEGRID_DATA = 'getSourceGridData';
const GET_SOURCEDROPDOWN_DATA = 'getSourceDropdownData';
const SAVE_DATA = 'saveData';
const LOAD_DATA = 'loadData';
const INITIAL_LOAD_DATA = 'initialLoadData';
const VALIDATE_CREDIT_CARD = 'validateCreditCard';
const VERIFY_EMAIL = 'verifyEmail';
const CHANGE_CREDIT_CARD = 'changeCreditCard';
const CALL_LOGGER = 'callLogger';
const CREATE_AIS_CASE = 'createAISCase';
const OPEN_AIS_CASE = 'openAISCase';
const UNDERWRITE_AIS_CASE = 'underwriteAISCase';
const GET_SMARTY_STREETS_PREDICTIONS = 'getSmartyStreetsPredictions';
const GET_GOOGLE_PLACES_PREDICTIONS = 'getGooglePlacesPredictions';
const GET_SMARTY_STREETS_DETAILS = 'getSmartyStreetsDetails';
const GET_GOOGLE_PLACES_DETAILS = 'getGooglePlacesDetails';
const RETRIEVE_PARAMED_SCHEDULER_URL = 'retrieveParamedSchedulerUrl';
const CHECK_PARAMED_SCHEDULER_STATUS = 'checkParamedSchedulerStatus';
const UPLOAD_FILE = 'uploadFile';
const SHOW_ATTACHMENTS = 'showAttachments';
const ON_DYNAMIC_TABLE_EVENT = 'onDynamicTableEvent';
const ON_FIELD_VALIDATE = 'onFieldValidate';

export {
    SEND_EMAIL,
    APPLY_ESIGN,
    DECLINE_ESIGN,
    ACTIVATE_FORM_PACKAGE,
    EXPORT_FORM_FROM_IGO,
    VIEW_PDF,
    CALL_CUSTOM_ASSEMBLY,
    CALL_JS_CUSTOM_ASSEMBLY,
    CALL_SERVER_PROCEDURE,
