import _ from 'lodash';
import logger from 'runtime/../../logger';
import makeNestedAppModalComponent from 'runtime/nested/makeModalComponent.react';
import { PACKAGE_GUID_KEY, SIGNER_GUID_KEY } from 'runtime/nested/esigui/constants/globalKeys';
import composeEsigUIAppConfigs from 'runtime/nested/esigui/utils/composeAppConfigs';
import mapHostClientDataToNested from 'runtime/nested/esigui/utils/mapHostClientDataToNested';
import injectGlobalData from 'runtime/nested/esigui/utils/injectGlobalData';
import getApplicationStaticPath from 'runtime/nested/utils/getApplicationStaticPath';
import * as pom from 'runtime/constants/MessageTypes';
import postMessageHelper from 'runtime/utils/postMessageHelper';
import { CLICKWRAP } from 'shared/constants/ScreenDestinations';
import getClientDataMapping from 'runtime/nested/esigui/utils/getClientDataMapping';
import { SHOULD_AUTOMATICALLY_VALIDATE } from 'runtime/constants/ClientDataFieldNames';

const isClickwrapDestiny = destiny => destiny === CLICKWRAP;

const specialClientDataMapping = {
    ESIG_packageGuid: PACKAGE_GUID_KEY,
    ESIG_signerGuid: SIGNER_GUID_KEY
};

/**
 * Shared SDK class. Added as a mixin to globally available SDK.
 */
export class SharedGlobalEventHandlers {
    /**
     * This method is invoked whenever Runtime attempts to open eSigUI.
     * @since v0.89.*
     * @param {string} versionRef - id of the version which represents the nested application
     */
    async ESIGUI_startSigning(versionRef) {
        postMessageHelper.sendMessageToParent({
            type: pom.START_ESIG_UI
        });

        const { clientData } = this.coreApi.getRuntimeState(
            this.coreApi.runtimeStateItems.CLIENT_DATA
        );

        const clientDataMapping = getClientDataMapping(versionRef);

        const eSigUiCaseData = mapHostClientDataToNested(clientData, clientDataMapping);

        const eSigServiceSpecificData = mapHostClientDataToNested(
            clientData,
            specialClientDataMapping
        );
        const esigServiceSpecificDataReadyToBeInjected = {
            ..._.mapValues(eSigServiceSpecificData, 'value')
        };

        const nestedAppScript = injectGlobalData(
            esigServiceSpecificDataReadyToBeInjected,
            eSigUiCaseData
        );

        const configs = composeEsigUIAppConfigs();

        const appStaticPath = getApplicationStaticPath();

        const indexHtml = await this.coreApi.generateIndexHtmlForNested(
            versionRef,
            appStaticPath,
            configs,
            nestedAppScript
        );

        const destiny = this.coreApi.getDestiny();

        const modalSettings = {
            animation: !isClickwrapDestiny(destiny),
            styles: {
                'nested-app__modal_fullscreen': isClickwrapDestiny(destiny)
            }
        };

        this.coreApi.showModal(makeNestedAppModalComponent(indexHtml, modalSettings));
    }

    /**
     * This method is invoked when nested application attempts to close eSigUI.
     * @since v0.92.*
     */
    async closeEsigUiApp() {
        postMessageHelper.sendMessageToParent({
            type: pom.FINISH_ESIG_UI
        });

        await this.loadData();

        this.coreApi.hideModal();
    }

    /**
     * Decides whether auto validation should start.
     * By default it looks if specific flag in client data is setup and case is not hard locked
     * @since v0.106.*
     * @returns {Boolean} - true, if auto validation should start, false if it isn't
     */
    needToAutoValidateScreens() {
        const skeys = this.coreApi.runtimeStateItems;
        const rs = this.coreApi.getRuntimeState([skeys.CLIENT_DATA, skeys.HARD_LOCK_STATUS]);
        const clientData = rs[skeys.CLIENT_DATA];
        const caseIsNotHardLocked = _.toString(rs[skeys.HARD_LOCK_STATUS]) !== 'true';

        const shouldAutomaticallyValidate = _.get(
            clientData,
            `${SHOULD_AUTOMATICALLY_VALIDATE}.value`,
            false
        );

        return _.toString(shouldAutomaticallyValidate) === 'true' && caseIsNotHardLocked;
    }

    /**
     * Starts auto validation of screens. Screens are open one by one in sequence automatically until last visible screen.
     * Auto validation starts from currently opened screen.
     * @since v0.106.*
     * @returns {Promise} - A Promise of operation completion if implementation contains any asynchronous operations and/or call to superclass.autoValidateScreens.
     * If implementation is synchronous you may return either resolved Promise or undefined.
     */
    async autoValidateScreens() {
        // Up to here state is restored from client data, 1st visible screen must be open and application waits for user input
        if (!this.needToAutoValidateScreens()) return false;

        const loaderId = 'autoValidateScreens';
        this.coreApi.showLoader(loaderId, 'Auto validation is in progress');

        // loader isn't showing if not waiting some time.
        await new Promise(r => setTimeout(r, 0));

        try {
            // open next visible screen until the latter
            while (await this.coreApi.openNextVisibleScreen()) {
                // Give some time to react to finish batch render update and, at the same time, prevent screens blinking too frequently
                await new Promise(r => setTimeout(r, 20));
            }

            // direct user to the first invalid screen
            await this.coreApi.openFirstIncompleteScreen();

            // remove from state flag `SHOULD_AUTOMATICALLY_VALIDATE`
            this.coreApi.updateRuntimeState({
                [this.coreApi.runtimeStateItems.CLIENT_DATA]: {
                    [SHOULD_AUTOMATICALLY_VALIDATE]: { value: '', formattedValue: '' }
                }
            });

            // send request to save client data
            await this.saveData();
        } catch (e) {
            logger.runtime.autoValidate.error(e);
        }

        this.coreApi.hideLoader(loaderId);
    }
}

# SDK Description

SDK allows to override/extend the following actions performed by SD Runtime:

* _saveData_ - invoked when Runtime wants to save client data on server;
* _loadData_ - invoked when Runtime wants to load client data from server asynchronously;
* _callCustomAssembly_ - invoked when Runtime calls a 3rd party service to perform calculations or
  retrieve some data;
* _callJsCustomAssembly_ - invoked when Runtime calls a custom javascript service to execute any
  custom logic;
* _getSourceGridData_ - invoked when Runtime requests data for a specific Source Grid control;
* _getSourceDropdownData_ - invoked when Runtime requests data for a specific Source Dropdown
  control;
* _validateCreditCard_ - invoked to validate credit card data via specific services;
* _changeCreditCard_ - invoked to erase previously entered credit card data;
* _verifyEmail_ - invoked whenever email control makes a call to validate email;
* _viewPDF_ - invoked whenever event action "View PDF" happened. This handler executes
  `RuntimeCoreAPI.showModal` method and pass there a component with pdf iframe inside. You could
  override this handler and show your own modal window components;
* _uploadFile_ - invoked whenever Runtime attempts to upload file;
* _showAttachments_ - invoked whenever Runtime attempts to show attachments.

Names of available actions are defined in the API constant
`global.App.runtimeCoreApi.sdkEventHandlerNames`. Detailed documentation of SDK actions is available
in `GlobalEventHandlers` section of this guide.

# Usage

SDK provides a JavaScript API to override/extend specified actions, access Runtime data, call 3rd
party services, transform data, etc.

SDK API is available via global JS function `createGlobalEventHandlersExtension(ExtensionClass)`.
The function is called by runtime upon its initialization and supplies the `ExtensionClass`
parameter, which can be used to access/override implementation of SDK actions.

The `createGlobalEventHandlersExtension` function **must be declared before the `runtime-xxx.js`
script**.

Example: The "scripts.html" file inside "application" folder of the downloaded runtime could be
modified in a way:

```html
<!-- Load custom script first -->
<script>
    function createGlobalEventHandlersExtension(ExtensionClass) {
        // your code here
    }
</script>

<!-- External libraries  -->
<script src="{{staticsPath}}/assets/jquery-3.3.1.min.js" defer></script>
<script src="{{staticsPath}}/assets/bootstrap-3.3.7.min.js" defer></script>

<!-- Then load runtime.js -->
<script src="{{staticsPath}}/assets/runtime.js" defer></script>
```

## External libraries

If there is necessity in external libraries load them in `scripts.html`. By default it contains
`jquery-3.3.1.min.js` and `bootstrap-3.3.7.min.js`. Replace these links if you need another versions
of libraries, but note that `runtime.js` is tested with `2.1.3` and `3.3.1` versions of `jQuery` and
`3.3.7` of `bootstrap`. Using other versions is possible but stable work is not guaranteed. Remember
that `runtime.js` doesn't work without `jQuery` and `bootstrap`.

In global scope available next libraries:

* React v15.1.0 https://github.com/facebook/react/tree/v15.0.1/docs
* ReactBootstrap v0.28.5 https://github.com/react-bootstrap/react-bootstrap/tree/v0.28.5/docs

```html
<!-- Examples -->
<script>
    function createGlobalEventHandlersExtension(ExtensionClass) {
        React.createElement(ReactBootstrap.Modal,
        {
            autoFocus: true,
            animation: false,
            show: true,
            backdrop: 'static',
            onHide: hideModal,
            bsSize: 'large'
        })
    }
</script>
```

## Default implementation

In order to keep default implementation of any action just don't override corresponding SDK action
or even don't implement the `createGlovalEventHandlersExtension` function. <br> By default all
methods inside ExtensionClass don't update UI (re-render view according to updated state). They are
called inside EventManager which manages asynchronous function calls and updates UI automatically
after completing calling current stack of asynchronous functions. In order to update UI whenever you
need call `refreshUI` method of `runtimeCoreApi`.

## Extending default implementation

Default implementation of any SDK action is available via `ExtensionClass.superclass` property. In
order to extend default action behavior you need to redefine corresponding SDK action and use the
`ExtensionClass.superclass` to access base implementation:

```javascript
function createGlobalEventHandlersExtension(ExtensionClass) {
    var callCustomAssemblyHandlerName =
        global.App.runtimeCoreApi.sdkEventHandlerNames.CALL_CUSTOM_ASSEMBLY;
    ExtensionClass.prototype[callCustomAssemblyHandlerName] = function(assemblyName) {
        // e.g. update PI_FirstName in runtime data
        this.coreApi.updateRuntimeData({
            caseData: [
                {
                    name: 'PI_FirstName',
                    value: 'Andrew',
                    formattedValue: 'Andrew',
                    isValid: 'true'
                }
            ],
            shouldReplaceSavedData: false
        });

        // call default implementation of callCustomAssembly and return the resulting promise,
        // which resolves in data provided by a custom assembly (if any)
        return this.superclass.callCustomAssembly(assemblyName);
    };
}
```

## Overriding default implementation

In order to override default action behavior you just need to redefine a corresponding SDK action in
ExtensionClass prototype:

```javascript
function createGlobalEventHandlersExtension(ExtensionClass) {
    var callCustomAssemblyHandlerName =
        global.App.runtimeCoreApi.sdkEventHandlerNames.CALL_CUSTOM_ASSEMBLY;
    ExtensionClass.prototype[callCustomAssemblyHandlerName] = function(assemblyName) {
        console.log("Don't really call:", assemblyName);
    };
}
```

## Runtime core API functions

Runtime API functions are available through `ExtensionClass.coreApi` property (available only inside
`ExtensionClass` methods). Also this `API` is exposed globally: `global.App.runtimeCoreApi`. <br>
Documentation of API methods is available in the `API` section of this guide.

## Embedding runtime into page

To do that, on your web page wrapper element for runtime should be created. Plus, you have to design
JS function which should find and return this wrapper element.

Example: The next wrapper is created: `<div id="runtime-entry"></div>` To embed runtime application
into this wrapper you need to create the next function:

```js
global.App.getRuntimeEntryPoint = function() {
    return document.querySelector('#runtime-entry');
};
```

This function should be defined after `global` is defined.

## Working with JavaScript Custom Assemblies

The JavaScript (JS) Custom Assemblies (CA) are services (technically just JS functions) which loaded
directly to client as script and work without any network activity, unlike default custom
assemblies. To load JS CA and make it available for the application it should be initialized in
`global.App.jsCA` object by default:

```html
<script>
    (function () {
        function setFullNameCaFunction() {
            // this points to ExtensionClass
            const clientData = this.coreApi.getRuntimeData();

            const firstName = clientData.FirstName.value;
            const lastName = clientData.LastName.value;
            const fullName = firstName + " " + lastName;

            this.coreApi.updateRuntimeData({
                caseData: [
                    {
                        name: 'FullName',
                        value: fullName,
                        formattedValue: fullName
                    }
                ]
            });
        }

        window.global = window.global || {};

        global.App = global.App || {};

        global.App.jsCA = {
            setFullNameCa: setFullNameCaFunction
        }
    })();
</script>
```

The format of `global.App.jsCA` object is the following:

```js
global.App.jsCA = { key: value };
```

where: `key` - name of the JS CA which is set to `call_js_service` action in screen designer.
`value` - JS function which should be executed when `call_js_service` action occurs.

**IMPORTANT** JS CA function must be implement in **synchronous** way while using it inside Rule's
`call_js_service` action. It possible to run asynchronous code ONLY for Event's `call_js_service`
action.

It is also possible to override default behaviour how to invoke JS CA. There is a method in SDK
`ExtensionClass` called `callJsCustomAssembly`. Please check section `Overriding default
implementation` to understand how to override it.

## Triggering Actions and Events

### General information

Application uses messages to communicate with its container (for example if it's opened in an
iframe). A container may send messages to the Application, thus requesting it to perform certain
actions. In its turn the Application sends messages to container to notify it about events which
happen inside the Application.

#### Incoming Messages (handled by Application)

In order to trigger an action in the Application, send a message to it. Syntax:

```js
applicationWindow.postMessage({ type: '<INCOMING_MESSAGE_TYPE>' [, data: <OPTIONAL_DATA_OBJECT>] }, '*');
```

All incoming messages generate a "feedback" message, which provides a status of the requested action
execution. See the "Outgoing Messages" section below with detailed description of these messages.

##### Types of incoming messages:

* `BEFORE_LEAVE_SCREEN_REQUEST`

Triggers the "Before Leave" action on a current screen.

```js
applicationWindow.postMessage({ type: 'BEFORE_LEAVE_SCREEN_REQUEST' }, '*');
```

* `SAVE_CLIENT_DATA_REQUEST`

Triggers saving of client data.

```js
applicationWindow.postMessage({ type: 'SAVE_CLIENT_DATA_REQUEST' }, '*');
```

* `VIEW_PDF_REQUEST`

Triggers the "View PDF" action.

```js
applicationWindow.postMessage({ type: 'VIEW_PDF_REQUEST' }, '*');
```

* `LOAD_CLIENT_DATA_REQUEST`

Triggers loading of client data either from server or from provided parameter. In the latter case no
server requests will be sent, provided values override current client data state.

NOTE: You can override default implementation of data loading. See `loadData` method in
GlobalEventHandlers.

```js
// load client data from server:
applicationWindow.postMessage({ type: 'LOAD_CLIENT_DATA_REQUEST' }, '*');

// load client data from provided data object:
applicationWindow.postMessage(
    {
        type: 'LOAD_CLIENT_DATA_REQUEST',
        clientData: {
            /* your data here */
        }
    },
    '*'
);
```

* `OPEN_SCREEN_REQUEST`

Switches to a screen with specified name.

```js
applicationWindow.postMessage(
    { type: 'OPEN_SCREEN_REQUEST', screenName: '<screen_name_to_open>' },
    '*'
);
```

#### Outgoing Messages (sent by Application)

In order to handle messages sent by the Application inside a container, use event listeners. Syntax:

```js
window.addEventListener('message', function(e) {
    if (e.data.type === OUTGOING_MESSAGE_TYPE) {
        // where OUTGOING_MESSAGE_TYPE is one of supported message types
        var messageData = e.data.data;
        // do something
    }
});
```

##### Types of outgoing messages :

* `OPEN_SCREEN_REQUEST_SUCCESS`, `OPEN_SCREEN_REQUEST_FAILED`

"Feedback" messages for `OPEN_SCREEN_REQUEST`.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'OPEN_SCREEN_REQUEST_SUCCESS') {
        // handle successful case
    }
    if (e.data.type === 'OPEN_SCREEN_REQUEST_FAILED') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `BEFORE_LEAVE_SCREEN_SUCCESS`, `BEFORE_LEAVE_SCREEN_FAILED`

"Feedback" messages for `BEFORE_LEAVE_SCREEN_REQUEST`.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'BEFORE_LEAVE_SCREEN_SUCCESS') {
        // handle successful case
    }
    if (e.data.type === 'BEFORE_LEAVE_SCREEN_FAILED') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `SAVE_CLIENT_DATA_SUCCESS`, `SAVE_CLIENT_DATA_FAILED`

"Feedback" messages for `SAVE_CLIENT_DATA_REQUEST`.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'SAVE_CLIENT_DATA_SUCCESS') {
        // handle successful case
    }
    if (e.data.type === 'SAVE_CLIENT_DATA_FAILED') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `VIEW_PDF_SUCCESS`, `VIEW_PDF_FAILED`

"Feedback" messages for `VIEW_PDF_REQUEST`.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'VIEW_PDF_SUCCESS') {
        // handle successful case
    }
    if (e.data.type === 'VIEW_PDF_FAILED') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `LOAD_CLIENT_DATA_SUCCESS`, `LOAD_CLIENT_DATA_FAILED`

"Feedback" messages for `LOAD_CLIENT_DATA_REQUEST`.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'LOAD_CLIENT_DATA_SUCCESS') {
        // handle successful case
    }
    if (e.data.type === 'LOAD_CLIENT_DATA_FAILED') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `BEFORE_LOAD_SCREEN_FAILED`, `BEFORE_LOAD_SCREEN_SUCCESS`

"Feedback" messages for internal action "Before Load Screen", which cannot be triggered from an
outside container and occurs whenever current screen switches to another one.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'BEFORE_LOAD_SCREEN_FAILED') {
        // handle successful case
    }
    if (e.data.type === 'BEFORE_LOAD_SCREEN_SUCCESS') {
        var error = e.data.data; // error object
        // handle error
    }
});
```

* `UPLOAD_FILE`

Fired when "Upload File" action is executed inside the Application.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'UPLOAD_FILE') {
        var defaultFileName = e.data.data; // default file name specified in the "UploadFile" action
        // process action
    }
});
```

* `SHOW_ATTACHMENT_LIST`

Fired when "Show Attachments" action is executed inside the Application.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'SHOW_ATTACHMENT_LIST') {
        // process action
    }
});
```

* `START_ESIG_UI`

Fired when "Start eSig UI" action is executed inside the Application.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'START_ESIG_UI') {
        // do appropriate stuff
    }
});
```

* `FINISH_ESIG_UI`

Fired when "Finish eSig UI" action is executed inside the Application.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'FINISH_ESIG_UI') {
        // do appropriate stuff
    }
});
```

* `MULTIPLE_EDITING_FAILED`

Fired when a server request returns the "MULTIPLE_EDITING_BLOCK" error.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'MULTIPLE_EDITING_FAILED') {
        // do appropriate stuff
    }
});
```

* `UPDATE_SESSION_TIMER_REQUEST`

Fired before each request to server to allow performing any required actions, e.g. update session
timer.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'UPDATE_SESSION_TIMER_REQUEST') {
        // process event
    }
});
```

* `UPDATE_FIRST_AND_LAST_NAME_REQUEST`

Fired on any value change inside the application. Contains values of "PIFirstName" and "PILastName"
client data items.

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'UPDATE_FIRST_AND_LAST_NAME_REQUEST') {
        var firstName = e.data.data.PIFirstName;
        var lastName = e.data.data.PILastName;
        // process retrieved values
    }
});
```

* `START_SCREEN_CHANGING`

Fired on navigation between screens. Indicates the start of the screen changing process. Contains
name of the next screen "screenName".

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'START_SCREEN_CHANGING') {
        var screenName = e.data.screenName;
        // process event
    }
});
```

* `FINISH_SCREEN_CHANGING`

Fired on navigation between screens. Indicates the finish of the screen changing process. Contains
name of the new screen "screenName".

```js
window.addEventListener('message', function(e) {
    if (e.data.type === 'FINISH_SCREEN_CHANGING') {
        var screenName = e.data.screenName;
        // process event
    }
});
```

## UI Configuration

You can use configs/uiConfig.json file to adopt the user interface to your company's specific
Requirements.

### Screen navigation panel

The screen navigation panel's visibility is handled changing show_nav_panel property: `true` to
show, `false` to hide. By default the screen navigation panel is visible(show_nav_pane: 'true').

### Backspace behavior

Backspace to go `back` action in non-Chrome browsers could be controlled by
`disable_backspace_navigation` property: `true` to disable moving backwards through the browser
history, `false` to enable moving backwards through browser history. Note that Chrome disables going
back by history by itself.

### Applying custom styles and fonts

Exported application includes all required stylesheets by default. However you can replace any
default stylesheet with custom one. To do it, open styles.html file and just update href attribute
of particular <link> (HTML element) to point it to your custom resource. All default stylesheets are
wrapped in comments with name of the resource that <link> element will load.

Also note that all style link elements should be put inside the wrapping <noscript id="deferred-styles">
tag to allow loading them in a deferred manner to avoid blocking the runtime application HTML rendering.
This is required for displaying the loading spinner.

styles.html

```html
<noscript id="deferred-styles">
    <!--fonts-->
        <link href="{{staticsPath}}/assets/runtime-fonts-fb9249627a6a1adfb091.css" rel="stylesheet">
    <!--fonts-->

    <!--font-awesome-->
        <link href="{{staticsPath}}/assets/runtime-font-awesome-50cc4c94e470f2e05632.css" rel="stylesheet">
    <!--font-awesome-->

    <!--bootstrap-theme-->
        <link href="{{staticsPath}}/assets/runtime-bootstrap-theme-9a029d11b10303249111.css" rel="stylesheet">
